mapper.select:
select
from mem_article inner join mem_account on mem_article.publisherId = mem_account.id left join (select articleId,count(articleId) as readCount from mem_article_read group by articleId)readInfo on readInfo.articleId = mem_article.id left join (select articleId,count(articleId) as replyCount from mem_article_reply group by articleId)replyInfo on replyInfo.articleId = mem_article.id
order by mem_article.publishTime desc order by ${sort} ${order}
limit #{offset}, #{limit}

mapper.update:
update mem_article
title = #{title}, content = #{content}, createTime = #{createTime}, publishTime = #{publishTime}, publisher = #{publisher}, publisherId = #{publisherId}, status = #{status}, channel = #{channel}, thumb = #{thumb}, summary = #{summary}, praiseCount = #{praiseCount}, shareCount = #{shareCount},
where id = #{id}

mapper.select:
select
	count(mem_article_read.id)
FROM  
	mem_article_read where 1=1
and  mem_article_read.id = #{id}
and  mem_article_read.articleId = #{articleId}

		select
			
		FROM  
			mem_article_read 
		inner join mem_account on mem_article_read.readerId = mem_account.id
		where 1=1
		and  mem_article_read.id = #{id}
		and  mem_article_read.articleId = #{articleId}
		and  mem_article_read.readerId = #{readerId}
		order by mem_article_read.readTime
        order by ${sort} ${order}
		
			
            	limit #{offset}, #{limit}

		select
			
		FROM  
			mem_article_reply 
		inner join mem_account on mem_article_reply.replierId = mem_account.id
		where 1=1
		and  mem_article_reply.id = #{id}
		and  mem_article_reply.articleId = #{articleId}
		and  mem_article_reply.replierId = #{replierId}
		order by mem_article_reply.replyTime
        order by ${sort} ${order}
		
			
            	limit #{offset}, #{limit}
